    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    display: grid;
    gap: 8rem;
    align-items: center;
  }
}

.image {
  position: relative;
  z-index: 1;

  figure {
    position: relative;
    img {
      width: 100%;
      position: relative;
      border-radius: var(--border-radius);
    }

    @media (min-width: 600px) {
      /* Background graphic */
      &:before {
        content: "";
        height: 100%;
        width: 100%;
        position: absolute;
        left: -2rem;
        top: -2rem;
        background: var(--color-shade, #fee2ee);
        border-radius: var(--border-radius);
      }
    }
  }
}

.body {
  display: grid;
  place-items: start;
  position: relative;
  z-index: 1;

  .headline {
    font-family: var(--font-heading);
    font-weight: 500;
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    line-height: clamp(2.5rem, 5vw, 3.875rem);
    margin-bottom: 1.5rem;
  }

  .subheading {
    margin-bottom: 2.15rem;
    color: var(--color-body-text);
  }

  .button {
  }
}

svg {
  width: 150px;
  position: absolute;
  top: 25%;
  right: 0;
  z-index: 0;
  transform-origin: right center;
  animation: pulse 7s ease-in-out 0.5s infinite alternate;

  @media (min-width: 600px) {
    top: -25%;
  }

  @media (prefers-reduced-motion) {
    animation: none;
  }
}
